
public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println( Double.MAX_VALUE);
		System.out.println( Integer.MAX_VALUE);
		System.out.println( Short.MAX_VALUE);
		System.out.println( Float.MAX_VALUE);
		System.out.println( Character.MAX_VALUE);		
		char letter = 'T' ;
		String booktitle = "Java in easy steps" ;
		int number = 365 ;
		float temperature = 38.46f ;
		boolean result = true ;
		byte temp = -128 ;
		short temp1 = -32000 ;
		long longint = 3788 ;
		double longfloat = 65788.999999 ;
		System.out.println("Initial is " + letter );
		System.out.println("Book is " + booktitle );
		System.out.println("Days is " + number );
		System.out.println("Temperature is " + temperature );
		System.out.println("Result is " + result);
		System.out.println("Long integer is " + longint);
		System.out.println("Long fload is " + longfloat);
	}

}
